SELECT b.marginalinfluence_idseedset, b.position, b.user_iduser AS "user_basic", c.user_iduser AS "user_complete", b.truespread_acc AS "truespread_basic", c.truespread_acc AS "truespread_complete" FROM tseedsdbbasic.marginalinfluenceseed b LEFT JOIN tseedsdbcomplete.marginalinfluenceseed c ON b.marginalinfluence_idseedset = c.marginalinfluence_idseedset AND b.position = c.position ORDER BY b.marginalinfluence_idseedset, b.position